<?phpdefined('_JEXEC') or die('Restricted access');JImport('b0.Post.PostKeys');//JImport('b0.fixtures');$params = $this->tmpl_params['list'];?><?php foreach ($this->items as $item) :?>	<article class="uk-article uk-visible-hover">		<!-- Панель управления -->		<?php if($item->controls){			echo JLayoutHelper::render('b0.controls-list', $item->controls);		}?>        <h2 class="uk-article-title uk-text-center-small">            <a href="<?= JRoute::_($item->url);?>" target="_blank">                <?= $item->title?>            </a>        </h2>		<div class="uk-grid" data-uk-grid-margin>			<div class="uk-width-medium-1-5 uk-text-center-small">				<?php if(isset($item->fields_by_key[PostKeys::KEY_IMAGE])) echo $item->fields_by_key[PostKeys::KEY_IMAGE]->result;?>			</div>			<div class="uk-width-medium-4-5 uk-text-center-small">		        <p class="uk-article-meta">		            <?= $item->ctime->format($params->get('tmpl_core.item_time_format'));?>		        </p>		        <p class="uk-article-lead">		            <?= $item->fields_by_key[PostKeys::KEY_ANNOUNCEMENT]->result;?>		        </p>			</div>		</div>	</article><?php endforeach;?>