<?phpdefined('_JEXEC') or die();if(!$this->items){	return;}JImport('b0.Work.Works');/** @var JRegistry $params */$paramsList = $this->tmpl_params['list'];$linkDiscounts = $this->tmpl_params['list']->get('tmpl_core.link_discounts') ?? '';$works = new Works($this->items, $paramsList);//b0debug($works);?><ul class="uk-grid uk-grid-width-medium-1-2 uk-grid-match" data-uk-grid-margin data-uk-grid-match="{target:'.uk-panel'}">	<?php foreach ($works->items as $id => $item) :?>        <li>            <div class="uk-panel uk-panel-box">                <div class="b0-title-related">                    <a href="<?= JRoute::_($item->url)?>" title="<?= $item->title?>" target="_blank">						<?= $item->title?>                    </a>                </div>				<?php $works->renderField('p', '', $item->subtitle) ?>                <hr class="uk-article-divider">                <div class="uk-grid">                    <div class="uk-width-1-3">						<?= $item->image ?>                    </div>                    <div class="uk-width-2-3">						<?php $works->renderPriceRelated($item, $linkDiscounts) ?>                    </div>                </div>            </div>        </li>	<?php endforeach;?></ul>