<?phpdefined('_JEXEC') or die('Restricted access');JImport('b0.Wiki.WikiIds');$paramsList = $this->tmpl_params['list'];?><?php foreach ($this->items as $item):?>	<article class="uk-article uk-visible-hover">        <!-- Панель управления -->		<?php if($item->controls){			echo JLayoutHelper::render('b0.controls-list', $item->controls);		}?>        <h2 class="uk-article-title">            <a href="<?= JRoute::_($item->url) ?>" target="_blank">                <?= $item->title?>            </a>        </h2>        <div class="uk-grid" data-uk-grid-margin>            <div class="uk-width-medium-2-10 uk-text-center-small">				<?= $item->fields_by_id[WikiIds::ID_IMAGE]->result ?? '<img src="'.$paramsList->get('tmpl_core.default_picture').				'" alt="' . $item->title . '" title="' . $item->title . '">' ?>            </div>            <div class="uk-width-medium-8-10 uk-text-center-small">                <p class="uk-article-meta">		            <?= $item->ctime->format($paramsList->get('tmpl_core.item_time_format')).' / просмотров: '.$item->hits ?>                </p>                <p class="uk-article-lead">		            <?= $item->fields_by_id[WikiIds::ID_ANNOUNCEMENT]->result ?>                </p>            </div>        </div>	</article><?php endforeach;?>